package com.shineon.coder.action ;import com.shineon.coder.service.convert.util.RmtOperateGroupInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.bind.annotation.RequestMapping;import com.shineon.coder.service.convert.CommonItem;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.dto.OperateGroupDTO;import com.shineon.coder.kernel.constant.action.OperateGroupControllerConstant;import org.springframework.web.bind.annotation.RequestBody;import com.alibaba.fastjson.JSONObject;import com.shineon.coder.db.common.ApiResultItem;import com.shineon.coder.db.pojo.RmtOperateGroupInfo;@RestController@Slf4jpublic class OperateGroupController {		@Autowired	OperateGroupDTO dto;		@Autowired	RmtOperateGroupInfoCommonUtil commonUtil;		@RequestMapping(OperateGroupControllerConstant.ACTION_GET)	public ApiResultItem get(@RequestBody JSONObject params) throws Exception{		RmtOperateGroupInfo pojo = new RmtOperateGroupInfo();		pojo.setId(params.getString("id"));		return dto.get(commonUtil.toCommon(pojo));	}		@RequestMapping(OperateGroupControllerConstant.ACTION_EDIT)	public ApiResultItem edit(@RequestBody JSONObject params) throws Exception{		RmtOperateGroupInfo pojo = new RmtOperateGroupInfo();		pojo.setId(params.getString("id"));		pojo.setOperategroupname(params.getString("operategroupname"));		pojo.setOperategrouptype(params.getInteger("operategrouptype"));		pojo.setBelongsystem(params.getString("belongsystem"));		pojo.setDescription(params.getString("description"));		pojo.setShoworder(params.getInteger("showorder"));		pojo.setReserved1(params.getInteger("reserved1"));		pojo.setReserved2(params.getString("reserved2"));		pojo.setOpreateset(params.getString("opreateset"));		return dto.edit(commonUtil.toCommon(pojo));	}		@RequestMapping(OperateGroupControllerConstant.ACTION_LIST)	public ApiResultItem list(@RequestBody JSONObject params) throws Exception{		return dto.list(commonUtil.success());	}		@RequestMapping(OperateGroupControllerConstant.ACTION_DELETE)	public ApiResultItem delete(@RequestBody JSONObject params) throws Exception{		RmtOperateGroupInfo pojo = new RmtOperateGroupInfo();		pojo.setId(params.getString("id"));		return dto.delete(commonUtil.toCommon(pojo));	}}