package com.shineon.coder.action ;import com.shineon.coder.db.pojo.RmtOrganizationInfo;import com.shineon.coder.service.convert.util.RmtOrganizationInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.bind.annotation.RequestMapping;import com.shineon.coder.service.convert.CommonItem;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.dto.OrganizationDTO;import com.shineon.coder.kernel.constant.action.OrganizationActionConstant;import org.springframework.web.bind.annotation.RequestBody;import com.alibaba.fastjson.JSONObject;import com.shineon.coder.db.common.ApiResultItem;@RestController@Slf4jpublic class OrganizationController {		@Autowired	OrganizationDTO dto;		@Autowired	RmtOrganizationInfoCommonUtil commonUtil;		@RequestMapping(OrganizationActionConstant.ACTION_GET)	public ApiResultItem get(@RequestBody JSONObject params) throws Exception{		RmtOrganizationInfo organizationInfo = new RmtOrganizationInfo();		organizationInfo.setId(params.getString("id"));		return dto.get(commonUtil.toCommon(organizationInfo));	}		@RequestMapping(OrganizationActionConstant.ACTION_EDIT)	public ApiResultItem edit(@RequestBody JSONObject params) throws Exception{		RmtOrganizationInfo organizationInfo = new RmtOrganizationInfo();		organizationInfo.setId(params.getString("id"));		organizationInfo.setOrganizationname(params.getString("organizationname"));		return dto.edit(commonUtil.toCommon(organizationInfo));	}		@RequestMapping(OrganizationActionConstant.ACTION_LIST)	public ApiResultItem list(@RequestBody JSONObject params) throws Exception{		return dto.list(commonUtil.success());	}		@RequestMapping(OrganizationActionConstant.ACTION_DELETE)	public ApiResultItem delete(@RequestBody JSONObject params) throws Exception{		RmtOrganizationInfo organizationInfo = new RmtOrganizationInfo();		organizationInfo.setId(params.getString("id"));		return dto.delete(commonUtil.toCommon(organizationInfo));	}}