package com.shineon.coder.action ;import com.shineon.coder.service.convert.util.RmtPrivilegeInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.bind.annotation.RequestMapping;import com.shineon.coder.service.convert.CommonItem;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.dto.PrivilegeDTO;import com.shineon.coder.kernel.constant.action.PrivilegeControllerConstant;import org.springframework.web.bind.annotation.RequestBody;import com.alibaba.fastjson.JSONObject;import com.shineon.coder.db.common.ApiResultItem;import com.shineon.coder.db.pojo.RmtPrivilegeInfo;@RestController@Slf4jpublic class PrivilegeController {	
	@Autowired	PrivilegeDTO dto;	
	@Autowired	RmtPrivilegeInfoCommonUtil commonUtil;	
	@RequestMapping(PrivilegeControllerConstant.ACTION_GET)	public ApiResultItem get(@RequestBody JSONObject params) throws Exception{		RmtPrivilegeInfo pojo = new RmtPrivilegeInfo();		pojo.setId(params.getString("id"));		return dto.get(commonUtil.toCommon(pojo));	}	
	@RequestMapping(PrivilegeControllerConstant.ACTION_EDIT)	public ApiResultItem edit(@RequestBody JSONObject params) throws Exception{		RmtPrivilegeInfo pojo = new RmtPrivilegeInfo();		pojo.setId(params.getString("id"));		pojo.setPrivilegename(params.getString("privilegename"));		pojo.setDescription(params.getString("description"));		pojo.setShoworder(params.getInteger("showorder"));		pojo.setReserved1(params.getInteger("reserved1"));		pojo.setReserved2(params.getString("reserved2"));		pojo.setOperategroup(params.getString("operategroup"));		return dto.edit(commonUtil.toCommon(pojo));	}	
	@RequestMapping(PrivilegeControllerConstant.ACTION_LIST)	public ApiResultItem list(@RequestBody JSONObject params) throws Exception{		return dto.list(commonUtil.success());	}	
	@RequestMapping(PrivilegeControllerConstant.ACTION_DELETE)	public ApiResultItem delete(@RequestBody JSONObject params) throws Exception{		RmtPrivilegeInfo pojo = new RmtPrivilegeInfo();		pojo.setId(params.getString("id"));		return dto.delete(commonUtil.toCommon(pojo));	}}