package com.shineon.coder.service.dto ;import com.shineon.coder.db.common.ApiResultItem;import com.shineon.coder.service.feign.UserFeign;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtUserInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtUserInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.CommonItem;import java.util.List;@Service@Slf4jpublic class UserDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	UserFeign userFeign;	@Autowired	RmtUserInfoCommonUtil commonUtil;		public RmtUserInfo get(CommonItem item) throws Exception {		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo( userFeign.get(item));	}		public ApiResultItem edit(CommonItem item) throws Exception {		CommonItem result = userFeign.edit(item);		return new ApiResultItem(result,commonUtil.toPojo(result));	}		public List<RmtUserInfo> list(CommonItem item) throws Exception {		QueryItem query = queryItemCommonUtil.toPojo(item);	 	CommonItem result = userFeign.list(item);		return commonUtil.toPojoList(result);	}	public ApiResultItem login(CommonItem item) throws Exception {		CommonItem result = userFeign.login(item);		return new ApiResultItem(result,commonUtil.toPojo(result));	}}