package com.shineon.coder.service.feign ;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.service.convert.CommonItem;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.RequestMapping;import com.shineon.coder.kernel.constant.feign.OperateFeignConstant;@FeignClient(name = OperateFeignConstant.FEIGN_SERVER_NAME,fallback = OperateFeignFallBack.class)public interface OperateFeign {	
	@RequestMapping(OperateFeignConstant. FEIGN_GET)	CommonItem get(CommonItem item);	
	@RequestMapping(OperateFeignConstant. FEIGN_EDIT)	CommonItem edit(CommonItem item);	
	@RequestMapping(OperateFeignConstant. FEIGN_LIST)	CommonItem list(CommonItem item);	
	@RequestMapping(OperateFeignConstant. FEIGN_DELETE)	CommonItem delete(CommonItem item);}