package com.shineon.coder.service.cache ;import com.shineon.coder.db.pojo.RmtPrivilegeInfo;import com.shineon.coder.service.convert.util.RmtPrivilegeInfoCommonUtil;import com.shineon.coder.service.feign.PrivilegeFeign;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.shineon.coder.kernel.util.SpringUtil;import com.shineon.coder.kernel.constant.cache.PrivilegeCacheConstant;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.CommonItem;import java.util.List;@Service@Slf4jpublic class PrivilegeCache extends IBaseCache<RmtPrivilegeInfo,RmtPrivilegeInfoCommonUtil> {	public void initCache()	{		setCommonutil(SpringUtil.getBean(RmtPrivilegeInfoCommonUtil.class));		setCacheDecorate(PrivilegeCacheConstant.CACHE_PRE,PrivilegeCacheConstant.CACHE_LAST);	}	@Autowired	PrivilegeFeign feign;	@Override	protected boolean check(String key) {		return true;	}		@Override	protected boolean success(String key, List<RmtPrivilegeInfo> pojos) {		return true;	}		@Override	protected boolean fail(String key, List<RmtPrivilegeInfo> pojos, Exception e) {		return true;	}		@Override	protected String getKeyParams(RmtPrivilegeInfo pojo) {		return pojo.getId();	}		@Override	protected CommonItem selectListByDB(QueryItem queryItem) {		return feign.list(queryItemCommonUtil.toCommon(queryItem));	}		@Override	protected CommonItem getPojoByDB(QueryItem queryItem) {		return feign.get(queryItemCommonUtil.toCommon(queryItem));	}		@Override	protected CommonItem updatePojoByDB(RmtPrivilegeInfo pojo) {		return feign.edit(commonutil.toCommon(pojo));	}		@Override	protected void deletePojoByDB(RmtPrivilegeInfo pojo) {		feign.delete(commonutil.toCommon(pojo));	}	}