package com.shineon.coder.service.cache ;import com.shineon.coder.db.pojo.RmtPrivilegeGroupInfo;import com.shineon.coder.service.convert.util.RmtPrivilegeGroupInfoCommonUtil;import com.shineon.coder.service.feign.PrivilegeGroupFeign;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.shineon.coder.kernel.util.SpringUtil;import com.shineon.coder.kernel.constant.cache.PrivilegeGroupCacheConstant;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.CommonItem;import java.util.List;@Service@Slf4jpublic class PrivilegeGroupCache extends IBaseCache<RmtPrivilegeGroupInfo,RmtPrivilegeGroupInfoCommonUtil> {	public void initCache()	{		setCommonutil(SpringUtil.getBean(RmtPrivilegeGroupInfoCommonUtil.class));		setCacheDecorate(PrivilegeGroupCacheConstant.CACHE_PRE,PrivilegeGroupCacheConstant.CACHE_LAST);	}		@Override	protected boolean check(String key) {		return true;	}		@Override	protected boolean success(String key, List<RmtPrivilegeGroupInfo> pojos) {		return true;	}		@Override	protected boolean fail(String key, List<RmtPrivilegeGroupInfo> pojos, Exception e) {		return true;	}		@Override	protected String getKeyParams(RmtPrivilegeGroupInfo pojo) {		return pojo.getId();	}		@Autowired	PrivilegeGroupFeign feign;	@Override	protected CommonItem selectListByDB(QueryItem queryItem) {		return feign.list(queryItemCommonUtil.toCommon(queryItem));	}		@Override	protected CommonItem getPojoByDB(QueryItem queryItem) {		return feign.get(queryItemCommonUtil.toCommon(queryItem));	}		@Override	protected CommonItem updatePojoByDB(RmtPrivilegeGroupInfo pojo) {		return feign.edit(commonutil.toCommon(pojo));	}		@Override	protected void deletePojoByDB(RmtPrivilegeGroupInfo pojo) {		feign.delete(commonutil.toCommon(pojo));	}	}