package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.pojo.MqMessage;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class MqMessageCommonBase implements CommonItemUtils<MqMessage>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( MqMessage pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParentCode1(pojo.getSourceSys());		data.setParentCode2(pojo.getDstSys());		data.setCreateName(pojo.getMessage());		data.setParent1Name(pojo.getMstype());		return data;	}	
	private MqMessage toPojoData( CommonData data) {		MqMessage pojo = new MqMessage();		pojo.setId(data.getId());		pojo.setSourceSys(data.getParentCode1());		pojo.setDstSys(data.getParentCode2());		pojo.setMessage(data.getCreateName());		pojo.setMstype(data.getParent1Name());		return pojo;	}	
	public CommonItem toCommon( MqMessage pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<MqMessage> pojos) {		List<CommonData> result = new ArrayList();		for(MqMessage item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public MqMessage toPojo( CommonItem item) throws Exception{		checkCommonItem(item);		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<MqMessage> toPojoList(  CommonItem item) throws Exception{		checkCommonItem(item);		List<MqMessage> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}