package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.pojo.RmtOperateGroupInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtOperateGroupInfoCommonBase implements CommonItemUtils<RmtOperateGroupInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtOperateGroupInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParentCode1(pojo.getOperategroupname());		data.setParentCode2(pojo.getBelongsystem());		data.setCreateId(pojo.getDescription());		data.setChangerId(pojo.getReserved2());		data.setCreateName(pojo.getOpreateset());		return data;	}	
	private RmtOperateGroupInfo toPojoData( CommonData data) {		RmtOperateGroupInfo pojo = new RmtOperateGroupInfo();		pojo.setId(data.getId());		pojo.setOperategroupname(data.getParentCode1());		pojo.setBelongsystem(data.getParentCode2());		pojo.setDescription(data.getCreateId());		pojo.setReserved2(data.getChangerId());		pojo.setOpreateset(data.getCreateName());		return pojo;	}	
	public CommonItem toCommon( RmtOperateGroupInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtOperateGroupInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtOperateGroupInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtOperateGroupInfo toPojo( CommonItem item) throws Exception{		checkCommonItem(item);		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtOperateGroupInfo> toPojoList(  CommonItem item) throws Exception{		checkCommonItem(item);		List<RmtOperateGroupInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}