package com.shineon.coder.service.dto ;import com.shineon.coder.service.cache.OrganizationCache;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtOrganizationInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtOrganizationInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.CommonItem;import java.util.List;@Service@Slf4jpublic class OrganizationDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	OrganizationCache cache;	@Autowired	RmtOrganizationInfoCommonUtil commonUtil;		public RmtOrganizationInfo get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.get(query);	}		public RmtOrganizationInfo edit(CommonItem item) throws Exception{		RmtOrganizationInfo organizationInfo = commonUtil.toPojo(item);		return cache.setCache(organizationInfo,true);	}		public List<RmtOrganizationInfo> list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.getListCache(query);	}}