package com.shineon.coder.service.dto ;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtPrivilegeGroupInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtPrivilegeGroupInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.CommonItem;import java.util.List;import com.shineon.coder.service.cache.PrivilegeGroupCache;@Service@Slf4jpublic class PrivilegeGroupDTO {	
	@Autowired	QueryItemCommonUtil queryItemCommonUtil;	
	@Autowired	RmtPrivilegeGroupInfoCommonUtil commonUtil;	
	@Autowired	PrivilegeGroupCache cache;	
	public RmtPrivilegeGroupInfo get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.get(query);	}	
	public RmtPrivilegeGroupInfo edit(CommonItem item) throws Exception{		RmtPrivilegeGroupInfo pojo = commonUtil.toPojo(item);		return cache.setCache(pojo,true);	}	
	public List<RmtPrivilegeGroupInfo> list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.getListCache(query);	}	
	public void delete(CommonItem item) throws Exception{		RmtPrivilegeGroupInfo pojo = commonUtil.toPojo(item);		cache.delete(pojo);	}}