package com.shineon.coder.service.dto ;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.db.pojo.RmtUserInfo;import com.shineon.coder.service.cache.UserCache;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service@Slf4jpublic class UserDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	UserCache cache;		public RmtUserInfo get(CommonItem item) throws Exception {		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.get(query);	}		public RmtUserInfo edit(CommonItem item) throws Exception {		QueryItem query = queryItemCommonUtil.toPojo(item);		RmtUserInfo userInfo = get(item);		cache.setCache(userInfo);		return get(item);	}		public List<RmtUserInfo> list(CommonItem item) throws Exception {		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.getListCache(query);	}	public RmtUserInfo getByName(CommonItem item) throws Exception {		QueryItem query = queryItemCommonUtil.toPojo(item);		return cache.get(query);	}}