package com.shineon.coder.service.dto ;import com.shineon.coder.service.cache.UserCache;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtUserInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtUserInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.feign.UserFeign;import com.shineon.coder.db.pojo.QueryItem;import java.util.List;@Service@Slf4jpublic class UserDTO {		@Autowired	UserCache cache;	@Autowired	UserFeign feign;	@Autowired	QueryItemCommonUtil commonUtil;	@Autowired	RmtUserInfoCommonUtil userInfoCommonUtil;	public RmtUserInfo get(QueryItem item) throws Exception {		return cache.get(item);	}		public RmtUserInfo edit(QueryItem item){		return userInfoCommonUtil.toPojo(feign.edit(commonUtil.toCommon(item)));	}		public List<RmtUserInfo> list(QueryItem item) throws Exception {		return cache.getListCache(item);	}}