package com.shineon.coder.service.mq.service ;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.cloud.stream.annotation.EnableBinding;import org.springframework.cloud.stream.annotation.StreamListener;import org.springframework.stereotype.Component;import com.shineon.coder.service.mq.client.UserNoticeMessageClient;@Component@EnableBinding(UserNoticeMessageClient.class)public class UserNoticeMessageService {	Logger logger = LoggerFactory.getLogger(UserNoticeMessageService.class);	@StreamListener(UserNoticeMessageClient.INPUTNAME)	public void receive(String message)	{		try {			Thread.sleep(1000*5);		} catch (InterruptedException e) {			e.printStackTrace();		}		logger.info("111111111111111111111111111111111111111111111111111");		logger.info("收到消息，请处理："+message);	}}