package com.shineon.coder.service.mq.service ;import com.alibaba.fastjson.JSON;import com.shineon.coder.service.cache.SysCache;import com.shineon.coder.service.convert.CommonItem;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.cloud.stream.annotation.EnableBinding;import org.springframework.cloud.stream.annotation.StreamListener;import org.springframework.stereotype.Component;import com.shineon.coder.service.mq.client.UserNoticeMessageClient;@Component@EnableBinding(UserNoticeMessageClient.class)public class UserNoticeMessageService {	Logger logger = LoggerFactory.getLogger(UserNoticeMessageService.class);	@StreamListener(UserNoticeMessageClient.INPUTNAME)	public void receive(String message)	{		logger.info("收到消息，请处理："+ message);		CommonItem item = JSON.parseObject(message,CommonItem.class);		SysCache.single.addCache(item);	}}