package com.shineon.coder.action ;import com.shineon.coder.service.convert.util.RmtUserInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.bind.annotation.RequestMapping;import com.shineon.coder.service.convert.CommonItem;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.dto.UserDTO;import com.shineon.coder.kernel.constant.action.UserActionConstant;import org.springframework.web.bind.annotation.RequestBody;@RestController@Slf4jpublic class UserController {		@Autowired	UserDTO dto;		@Autowired	RmtUserInfoCommonUtil commonUtil;		@RequestMapping(UserActionConstant.ACTION_GET)	public CommonItem get(@RequestBody CommonItem item) throws Exception {		return commonUtil.toCommon(dto.get(item));	}		@RequestMapping(UserActionConstant.ACTION_EDIT)	public CommonItem edit(@RequestBody CommonItem item) throws Exception {		return commonUtil.toCommon(dto.edit(item));	}		@RequestMapping(UserActionConstant.ACTION_LIST)	public CommonItem list(@RequestBody CommonItem item) throws Exception {		return commonUtil.toCommon(dto.list(item));	}	@RequestMapping(UserActionConstant.ACTION_DELETE)	public CommonItem delete(@RequestBody CommonItem item) throws Exception {		return commonUtil.toCommon(dto.delete(item));	}	@RequestMapping(UserActionConstant.ACTION_GETBYNAME)	public CommonItem getByName(@RequestBody CommonItem item) throws Exception {		return commonUtil.toCommon(dto.getByName(item));	}}