package com.shineon.coder.service.bo ;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import com.shineon.coder.db.sql.mergedao.IRmtOperateInfoMapper;import com.shineon.coder.db.sql.pojo.RmtOperateInfo;import java.util.List;import com.shineon.coder.kernel.util.GuidUtil;@Repositorypublic class OperateBO {		@Autowired	private IRmtOperateInfoMapper mapper;		@Autowired	private GuidUtil guidUtil;		public RmtOperateInfo get(String id){		return mapper.selectByPrimaryKey(id);	}		public RmtOperateInfo insert(RmtOperateInfo item){		String id = guidUtil.gen();		item.setId(id);		mapper.insertByCustomId(item);		return get(id);	}		public List<RmtOperateInfo> list() {		return mapper.list(null,null);	}		public RmtOperateInfo update(RmtOperateInfo item){		mapper.updateByPrimaryKey(item);		return get(item.getId());	}		public boolean delete(String id){		int count = mapper.deleteByPrimaryKey(id);		if(count>0) { return true; }		return false;	}	}