package com.shineon.coder.service.bo ;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import com.shineon.coder.db.sql.mergedao.IRmtOperateGroupInfoMapper;import com.shineon.coder.db.sql.pojo.RmtOperateGroupInfo;import java.util.List;import com.shineon.coder.kernel.util.GuidUtil;@Repositorypublic class OperateGroupBO {		@Autowired	private IRmtOperateGroupInfoMapper mapper;		@Autowired	private GuidUtil guidUtil;		public RmtOperateGroupInfo get(String id){		return mapper.selectByPrimaryKey(id);	}		public RmtOperateGroupInfo insert(RmtOperateGroupInfo item){		String id = guidUtil.gen();		item.setId(id);		mapper.insertByCustomId(item);		return get(id);	}		public List<RmtOperateGroupInfo> list() {		return mapper.list(null,null);	}		public RmtOperateGroupInfo update(RmtOperateGroupInfo item){		mapper.updateByPrimaryKey(item);		return get(item.getId());	}		public boolean delete(String id){		int count = mapper.deleteByPrimaryKey(id);		if(count>0) { return true; }		return false;	}	}