package com.shineon.coder.service.bo ;import com.shineon.coder.db.sql.mergedao.IRmtPrivilegeGroupInfoMapper;import com.shineon.coder.db.sql.pojo.RmtPrivilegeGroupInfo;import com.shineon.coder.kernel.util.GuidUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic class PrivilegeGroupBO {		@Autowired	private IRmtPrivilegeGroupInfoMapper mapper;		@Autowired	private GuidUtil guidUtil;		public RmtPrivilegeGroupInfo get(String id){		return mapper.selectByPrimaryKey(id);	}		public RmtPrivilegeGroupInfo insert(RmtPrivilegeGroupInfo item){		String id = guidUtil.gen();		item.setId(id);		mapper.insertByCustomId(item);		return get(id);	}		public List<RmtPrivilegeGroupInfo> list() {		return mapper.list(null,null);	}		public RmtPrivilegeGroupInfo update(RmtPrivilegeGroupInfo item){		mapper.updateByPrimaryKey(item);		return get(item.getId());	}		public boolean delete(String id){		int count = mapper.deleteByPrimaryKey(id);		if(count>0) { return true; }		return false;	}	}