package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtActiorInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtActiorInfoCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtActiorInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getActorname());		data.setCreateTime(pojo.getCreatetime());		data.setPtitle(pojo.getDescription());		data.setCreator(pojo.getSystemactorflag());		data.setTitle(pojo.getReserved2());		data.setGroup(pojo.getPrivilegegroup());		return data;	}	
	private RmtActiorInfo toPojoData( CommonData data) {		RmtActiorInfo pojo = new RmtActiorInfo();		pojo.setId(data.getId());		pojo.setActorname(data.getParent());		pojo.setCreatetime(data.getCreateTime());		pojo.setDescription(data.getPtitle());		pojo.setSystemactorflag(data.getCreator());		pojo.setReserved2(data.getTitle());		pojo.setPrivilegegroup(data.getGroup());		return pojo;	}	
	public CommonItem toCommon( RmtActiorInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtActiorInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtActiorInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtActiorInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtActiorInfo> toPojoList(  CommonItem item) {		List<RmtActiorInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}