package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtDepartmentChartInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtDepartmentChartInfoCommonBase implements CommonItemUtils<RmtDepartmentChartInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtDepartmentChartInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParentCode1(pojo.getDepartmentid());		data.setParent1Name(pojo.getDepartmentname());		data.setParentCode2(pojo.getOrganizationid());		data.setTitle(pojo.getLevelinfo());		data.setCreateId(pojo.getParentid());		data.setCreateTime(pojo.getCreatedtime());		data.setCreateName(pojo.getCreateuserid());		data.setBeginTime(pojo.getModifytime());		data.setChangerId(pojo.getModifyuserid());		data.setParent2Name(pojo.getReserved2());		return data;	}	
	private RmtDepartmentChartInfo toPojoData( CommonData data) {		RmtDepartmentChartInfo pojo = new RmtDepartmentChartInfo();		pojo.setId(data.getId());		pojo.setDepartmentid(data.getParentCode1());		pojo.setDepartmentname(data.getParent1Name());		pojo.setOrganizationid(data.getParentCode2());		pojo.setLevelinfo(data.getTitle());		pojo.setParentid(data.getCreateId());		pojo.setCreatedtime(data.getCreateTime());		pojo.setCreateuserid(data.getCreateName());		pojo.setModifytime(data.getBeginTime());		pojo.setModifyuserid(data.getChangerId());		pojo.setReserved2(data.getParent2Name());		return pojo;	}	
	public CommonItem toCommon( RmtDepartmentChartInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtDepartmentChartInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtDepartmentChartInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtDepartmentChartInfo toPojo( CommonItem item) throws Exception{		checkCommonItem(item);		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtDepartmentChartInfo> toPojoList(  CommonItem item) throws Exception{		checkCommonItem(item);		List<RmtDepartmentChartInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}