package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtDepartmentChartInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtDepartmentChartInfoCommonBase implements CommonItemUtils<RmtDepartmentChartInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtDepartmentChartInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getDepartmentid());		data.setSortIndex(pojo.getDepartmentcode());		data.setCreator(pojo.getDepartmentname());		data.setContent(pojo.getOrganizationid());		data.setTitle(pojo.getLevelinfo());		data.setPtitle(pojo.getParentid());		data.setCreateTime(pojo.getCreatedtime());		data.setGroup(pojo.getCreateuserid());		data.setBeginTime(pojo.getModifytime());		data.setThumb(pojo.getModifyuserid());		return data;	}	
	private RmtDepartmentChartInfo toPojoData( CommonData data) {		RmtDepartmentChartInfo pojo = new RmtDepartmentChartInfo();		pojo.setId(data.getId());		pojo.setDepartmentid(data.getParent());		pojo.setDepartmentcode(data.getSortIndex());		pojo.setDepartmentname(data.getCreator());		pojo.setOrganizationid(data.getContent());		pojo.setLevelinfo(data.getTitle());		pojo.setParentid(data.getPtitle());		pojo.setCreatedtime(data.getCreateTime());		pojo.setCreateuserid(data.getGroup());		pojo.setModifytime(data.getBeginTime());		pojo.setModifyuserid(data.getThumb());		return pojo;	}	
	public CommonItem toCommon( RmtDepartmentChartInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtDepartmentChartInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtDepartmentChartInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtDepartmentChartInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtDepartmentChartInfo> toPojoList(  CommonItem item) {		List<RmtDepartmentChartInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}