package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtDepartmentExtend;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtDepartmentExtendCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtDepartmentExtend pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getDepartmentid());		data.setCreator(pojo.getDepartmentaddress());		return data;	}	
	private RmtDepartmentExtend toPojoData( CommonData data) {		RmtDepartmentExtend pojo = new RmtDepartmentExtend();		pojo.setId(data.getId());		pojo.setDepartmentid(data.getParent());		pojo.setDepartmentaddress(data.getCreator());		return pojo;	}	
	public CommonItem toCommon( RmtDepartmentExtend pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtDepartmentExtend> pojos) {		List<CommonData> result = new ArrayList();		for(RmtDepartmentExtend item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtDepartmentExtend toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtDepartmentExtend> toPojoList(  CommonItem item) {		List<RmtDepartmentExtend> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}