package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtDepartmentInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtDepartmentInfoCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtDepartmentInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getDepartmentid());		data.setCreator(pojo.getDepartmentname());		data.setContent(pojo.getDepartmenttype());		data.setPtitle(pojo.getDescription());		data.setCreateTime(pojo.getCreatedtime());		data.setTitle(pojo.getCreateuserid());		data.setBeginTime(pojo.getModifytime());		data.setGroup(pojo.getModifyuserid());		data.setEndTime(pojo.getDeletedtime());		data.setThumb(pojo.getDeleteduserid());		return data;	}	
	private RmtDepartmentInfo toPojoData( CommonData data) {		RmtDepartmentInfo pojo = new RmtDepartmentInfo();		pojo.setId(data.getId());		pojo.setDepartmentid(data.getParent());		pojo.setDepartmentname(data.getCreator());		pojo.setDepartmenttype(data.getContent());		pojo.setDescription(data.getPtitle());		pojo.setCreatedtime(data.getCreateTime());		pojo.setCreateuserid(data.getTitle());		pojo.setModifytime(data.getBeginTime());		pojo.setModifyuserid(data.getGroup());		pojo.setDeletedtime(data.getEndTime());		pojo.setDeleteduserid(data.getThumb());		return pojo;	}	
	public CommonItem toCommon( RmtDepartmentInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtDepartmentInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtDepartmentInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtDepartmentInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtDepartmentInfo> toPojoList(  CommonItem item) {		List<RmtDepartmentInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}