package com.shineon.coder.service.convert.base ;import com.shineon.coder.db.sql.pojo.RmtOperateGroup;import com.shineon.coder.service.convert.CommonData;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.util.ArrayList;import java.util.List;public class RmtOperateGroupCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());		private CommonData toCommonData( RmtOperateGroup pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setCreator(pojo.getOperategroupname());		data.setContent(pojo.getBelongsystem());		data.setParent(pojo.getDescription());		data.setPtitle(pojo.getOpreateset());		return data;	}		private RmtOperateGroup toPojoData( CommonData data) {		RmtOperateGroup pojo = new RmtOperateGroup();		pojo.setId(data.getId());		pojo.setOperategroupname(data.getCreator());		pojo.setBelongsystem(data.getContent());		pojo.setDescription(data.getParent());		pojo.setOpreateset(data.getPtitle());		return pojo;	}		public CommonItem toCommon( RmtOperateGroup pojo) {		return success(toCommonData(pojo));	}		public  CommonItem toCommon( List<RmtOperateGroup> pojos) {		List<CommonData> result = new ArrayList();		for(RmtOperateGroup item : pojos){			result.add(toCommonData(item));		}		return success(result);	}		public RmtOperateGroup toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}		public List<RmtOperateGroup> toPojoList(  CommonItem item) {		List<RmtOperateGroup> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		}