package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtOrganizationChartInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtOrganizationChartInfoCommonBase implements CommonItemUtils<RmtOrganizationChartInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtOrganizationChartInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getOrganizationid());		data.setSortIndex(pojo.getOrganizationcode());		data.setCreator(pojo.getOrganizationname());		data.setTitle(pojo.getLevelinfo());		data.setPtitle(pojo.getParentid());		data.setCreateTime(pojo.getCreatedtime());		data.setContent(pojo.getCreateuserid());		data.setBeginTime(pojo.getModifytime());		data.setGroup(pojo.getModifyuserid());		data.setThumb(pojo.getReserved2());		return data;	}	
	private RmtOrganizationChartInfo toPojoData( CommonData data) {		RmtOrganizationChartInfo pojo = new RmtOrganizationChartInfo();		pojo.setId(data.getId());		pojo.setOrganizationid(data.getParent());		pojo.setOrganizationcode(data.getSortIndex());		pojo.setOrganizationname(data.getCreator());		pojo.setLevelinfo(data.getTitle());		pojo.setParentid(data.getPtitle());		pojo.setCreatedtime(data.getCreateTime());		pojo.setCreateuserid(data.getContent());		pojo.setModifytime(data.getBeginTime());		pojo.setModifyuserid(data.getGroup());		pojo.setReserved2(data.getThumb());		return pojo;	}	
	public CommonItem toCommon( RmtOrganizationChartInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtOrganizationChartInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtOrganizationChartInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtOrganizationChartInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtOrganizationChartInfo> toPojoList(  CommonItem item) {		List<RmtOrganizationChartInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}