package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtOrganizationDepartmentInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtOrganizationDepartmentInfoCommonBase implements CommonItemUtils<RmtOrganizationDepartmentInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtOrganizationDepartmentInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getDepartementid());		data.setCreator(pojo.getOrganizationid());		data.setSortIndex(pojo.getReserved1());		data.setTitle(pojo.getReserved2());		return data;	}	
	private RmtOrganizationDepartmentInfo toPojoData( CommonData data) {		RmtOrganizationDepartmentInfo pojo = new RmtOrganizationDepartmentInfo();		pojo.setId(data.getId());		pojo.setDepartementid(data.getParent());		pojo.setOrganizationid(data.getCreator());		pojo.setReserved1(data.getSortIndex());		pojo.setReserved2(data.getTitle());		return pojo;	}	
	public CommonItem toCommon( RmtOrganizationDepartmentInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtOrganizationDepartmentInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtOrganizationDepartmentInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtOrganizationDepartmentInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtOrganizationDepartmentInfo> toPojoList(  CommonItem item) {		List<RmtOrganizationDepartmentInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}