package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtOrganizationExtendInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtOrganizationExtendInfoCommonBase implements CommonItemUtils<RmtOrganizationExtendInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtOrganizationExtendInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getOrganizationid());		data.setCreator(pojo.getOrganizationaddress());		data.setSortIndex(pojo.getReserved1());		data.setTitle(pojo.getReserved2());		return data;	}	
	private RmtOrganizationExtendInfo toPojoData( CommonData data) {		RmtOrganizationExtendInfo pojo = new RmtOrganizationExtendInfo();		pojo.setId(data.getId());		pojo.setOrganizationid(data.getParent());		pojo.setOrganizationaddress(data.getCreator());		pojo.setReserved1(data.getSortIndex());		pojo.setReserved2(data.getTitle());		return pojo;	}	
	public CommonItem toCommon( RmtOrganizationExtendInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtOrganizationExtendInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtOrganizationExtendInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtOrganizationExtendInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtOrganizationExtendInfo> toPojoList(  CommonItem item) {		List<RmtOrganizationExtendInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}