package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtPrivilegeInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtPrivilegeInfoCommonBase implements CommonItemUtils<RmtPrivilegeInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtPrivilegeInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getPrivilegename());		data.setPtitle(pojo.getDescription());		data.setSortIndex(pojo.getShoworder());		data.setCreator(pojo.getReserved2());		data.setGroup(pojo.getOperategroup());		return data;	}	
	private RmtPrivilegeInfo toPojoData( CommonData data) {		RmtPrivilegeInfo pojo = new RmtPrivilegeInfo();		pojo.setId(data.getId());		pojo.setPrivilegename(data.getParent());		pojo.setDescription(data.getPtitle());		pojo.setShoworder(data.getSortIndex());		pojo.setReserved2(data.getCreator());		pojo.setOperategroup(data.getGroup());		return pojo;	}	
	public CommonItem toCommon( RmtPrivilegeInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtPrivilegeInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtPrivilegeInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtPrivilegeInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtPrivilegeInfo> toPojoList(  CommonItem item) {		List<RmtPrivilegeInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}