package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtTenantChartInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtTenantChartInfoCommonBase implements CommonItemUtils<RmtTenantChartInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtTenantChartInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getTenantid());		data.setContent(pojo.getTenantname());		data.setSortIndex(pojo.getTenantcode());		data.setTitle(pojo.getLevelinfo());		data.setCreator(pojo.getParentid());		data.setCreateTime(pojo.getCreatetime());		data.setPtitle(pojo.getCreateuserid());		data.setBeginTime(pojo.getModifytime());		data.setGroup(pojo.getModifyuserid());		data.setThumb(pojo.getReserved2());		return data;	}	
	private RmtTenantChartInfo toPojoData( CommonData data) {		RmtTenantChartInfo pojo = new RmtTenantChartInfo();		pojo.setId(data.getId());		pojo.setTenantid(data.getParent());		pojo.setTenantname(data.getContent());		pojo.setTenantcode(data.getSortIndex());		pojo.setLevelinfo(data.getTitle());		pojo.setParentid(data.getCreator());		pojo.setCreatetime(data.getCreateTime());		pojo.setCreateuserid(data.getPtitle());		pojo.setModifytime(data.getBeginTime());		pojo.setModifyuserid(data.getGroup());		pojo.setReserved2(data.getThumb());		return pojo;	}	
	public CommonItem toCommon( RmtTenantChartInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtTenantChartInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtTenantChartInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtTenantChartInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtTenantChartInfo> toPojoList(  CommonItem item) {		List<RmtTenantChartInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}