package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtTenantExtendInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtTenantExtendInfoCommonBase implements CommonItemUtils<RmtTenantExtendInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtTenantExtendInfo pojo) {		CommonData data = new CommonData();		data.setSortIndex(pojo.getId());		data.setParent(pojo.getTenantid());		data.setContent(pojo.getTenantname());		data.setPtitle(pojo.getDescription());		data.setCreator(pojo.getReserved2());		return data;	}	
	private RmtTenantExtendInfo toPojoData( CommonData data) {		RmtTenantExtendInfo pojo = new RmtTenantExtendInfo();		pojo.setId(data.getSortIndex());		pojo.setTenantid(data.getParent());		pojo.setTenantname(data.getContent());		pojo.setDescription(data.getPtitle());		pojo.setReserved2(data.getCreator());		return pojo;	}	
	public CommonItem toCommon( RmtTenantExtendInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtTenantExtendInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtTenantExtendInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtTenantExtendInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtTenantExtendInfo> toPojoList(  CommonItem item) {		List<RmtTenantExtendInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}