package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtTenantInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtTenantInfoCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtTenantInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getTenantid());		data.setContent(pojo.getTenantname());		data.setCreateTime(pojo.getCreatetime());		data.setCreator(pojo.getCreateduserid());		data.setPtitle(pojo.getOrganizationid());		data.setBeginTime(pojo.getTenantexpirydate());		data.setTitle(pojo.getTenantextendid());		data.setGroup(pojo.getReserved2());		data.setThumb(pojo.getTenantadminset());		return data;	}	
	private RmtTenantInfo toPojoData( CommonData data) {		RmtTenantInfo pojo = new RmtTenantInfo();		pojo.setId(data.getId());		pojo.setTenantid(data.getParent());		pojo.setTenantname(data.getContent());		pojo.setCreatetime(data.getCreateTime());		pojo.setCreateduserid(data.getCreator());		pojo.setOrganizationid(data.getPtitle());		pojo.setTenantexpirydate(data.getBeginTime());		pojo.setTenantextendid(data.getTitle());		pojo.setReserved2(data.getGroup());		pojo.setTenantadminset(data.getThumb());		return pojo;	}	
	public CommonItem toCommon( RmtTenantInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtTenantInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtTenantInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtTenantInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtTenantInfo> toPojoList(  CommonItem item) {		List<RmtTenantInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}