package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtUserActorInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtUserActorInfoCommonBase implements CommonItemUtils<RmtUserActorInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtUserActorInfo pojo) {		CommonData data = new CommonData();		data.setParentCode1(pojo.getDepartmentid());		data.setParentCode2(pojo.getActorgroup());		data.setChangerId(pojo.getSpeciallygroup());		data.setCreateTime(pojo.getCreatetime());		data.setCreateId(pojo.getCreateuserid());		data.setParent1Name(pojo.getDescription());		data.setCreateName(pojo.getReserved1());		data.setParent2Name(pojo.getReserved2());		return data;	}	
	private RmtUserActorInfo toPojoData( CommonData data) {		RmtUserActorInfo pojo = new RmtUserActorInfo();		pojo.setDepartmentid(data.getParentCode1());		pojo.setActorgroup(data.getParentCode2());		pojo.setSpeciallygroup(data.getChangerId());		pojo.setCreatetime(data.getCreateTime());		pojo.setCreateuserid(data.getCreateId());		pojo.setDescription(data.getParent1Name());		pojo.setReserved1(data.getCreateName());		pojo.setReserved2(data.getParent2Name());		return pojo;	}	
	public CommonItem toCommon( RmtUserActorInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtUserActorInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtUserActorInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public com.shineon.coder.db.sql.pojo.RmtUserActorInfo toPojo( CommonItem item) throws Exception{		checkCommonItem(item);		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<com.shineon.coder.db.sql.pojo.RmtUserActorInfo> toPojoList(  CommonItem item) throws Exception{		checkCommonItem(item);		List<com.shineon.coder.db.sql.pojo.RmtUserActorInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
		}