package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtUserDepartmentInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtUserDepartmentInfoCommonBase implements CommonItemUtils<RmtUserDepartmentInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtUserDepartmentInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getUserid());		data.setCreator(pojo.getDepartmentid());		data.setSortIndex(pojo.getShoworder());		data.setTitle(pojo.getReserved2());		return data;	}	
	private RmtUserDepartmentInfo toPojoData( CommonData data) {		RmtUserDepartmentInfo pojo = new RmtUserDepartmentInfo();		pojo.setId(data.getId());		pojo.setUserid(data.getParent());		pojo.setDepartmentid(data.getCreator());		pojo.setShoworder(data.getSortIndex());		pojo.setReserved2(data.getTitle());		return pojo;	}	
	public CommonItem toCommon( RmtUserDepartmentInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtUserDepartmentInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtUserDepartmentInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtUserDepartmentInfo toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtUserDepartmentInfo> toPojoList(  CommonItem item) {		List<RmtUserDepartmentInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}