package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtUserExtend;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtUserExtendCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtUserExtend pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getUserid());		data.setCreator(pojo.getRealname());		data.setTitle(pojo.getSpellname());		data.setContent(pojo.getPostname());		data.setPtitle(pojo.getDutiesname());		data.setGroup(pojo.getOfficeaddress());		data.setThumb(pojo.getMobilephone());		return data;	}	
	private RmtUserExtend toPojoData( CommonData data) {		RmtUserExtend pojo = new RmtUserExtend();		pojo.setId(data.getId());		pojo.setUserid(data.getParent());		pojo.setRealname(data.getCreator());		pojo.setSpellname(data.getTitle());		pojo.setPostname(data.getContent());		pojo.setDutiesname(data.getPtitle());		pojo.setOfficeaddress(data.getGroup());		pojo.setMobilephone(data.getThumb());		return pojo;	}	
	public CommonItem toCommon( RmtUserExtend pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtUserExtend> pojos) {		List<CommonData> result = new ArrayList();		for(RmtUserExtend item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtUserExtend toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtUserExtend> toPojoList(  CommonItem item) {		List<RmtUserExtend> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}