package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtUserLogin;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtUserLoginCommonBase implements CommonItemUtils{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtUserLogin pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getUserid());		data.setTitle(pojo.getLoginip());		data.setCreateTime(pojo.getLasttimelogin());		return data;	}	
	private RmtUserLogin toPojoData( CommonData data) {		RmtUserLogin pojo = new RmtUserLogin();		pojo.setId(data.getId());		pojo.setUserid(data.getParent());		pojo.setLoginip(data.getTitle());		pojo.setLasttimelogin(data.getCreateTime());		return pojo;	}	
	public CommonItem toCommon( RmtUserLogin pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtUserLogin> pojos) {		List<CommonData> result = new ArrayList();		for(RmtUserLogin item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public RmtUserLogin toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<RmtUserLogin> toPojoList(  CommonItem item) {		List<RmtUserLogin> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}