package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.sql.pojo.RmtUserLoginInfo;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtUserLoginInfoCommonBase implements CommonItemUtils<RmtUserLoginInfo>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( RmtUserLoginInfo pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParentCode1(pojo.getUserid());		data.setTitle(pojo.getLoginip());		data.setCreateTime(pojo.getLasttimelogin());		data.setParentCode2(pojo.getReserved2());		return data;	}	
	private RmtUserLoginInfo toPojoData( CommonData data) {		RmtUserLoginInfo pojo = new RmtUserLoginInfo();		pojo.setId(data.getId());		pojo.setUserid(data.getParentCode1());		pojo.setLoginip(data.getTitle());		pojo.setLasttimelogin(data.getCreateTime());		pojo.setReserved2(data.getParentCode2());		return pojo;	}	
	public CommonItem toCommon( RmtUserLoginInfo pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<RmtUserLoginInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtUserLoginInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public com.shineon.coder.db.sql.pojo.RmtUserLoginInfo toPojo( CommonItem item) throws Exception{		checkCommonItem(item);		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<com.shineon.coder.db.sql.pojo.RmtUserLoginInfo> toPojoList(  CommonItem item) throws Exception{		checkCommonItem(item);		List<com.shineon.coder.db.sql.pojo.RmtUserLoginInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
		}