package com.shineon.coder.service.dto ;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.db.sql.pojo.RmtUserInfo;import com.shineon.coder.service.bo.UserBO;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.util.RmtUserInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service@Slf4jpublic class UserDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	UserBO bo;	@Autowired	RmtUserInfoCommonUtil commonUtil;	public RmtUserInfo get(CommonItem item){		QueryItem query = queryItemCommonUtil.toPojo(item);		return bo.get(query.getId());	}		public RmtUserInfo edit(CommonItem item){		RmtUserInfo userInfo = commonUtil.toPojo(item);		if(userInfo.getId() ==null||userInfo.getId().equals("0")) {			bo.add(userInfo);		}		else{			bo.update(userInfo);		}		return bo.get(userInfo.getId());	}		public List<RmtUserInfo> list(CommonItem item){		QueryItem query = queryItemCommonUtil.toPojo(item);		return bo.list();	}	public RmtUserInfo getByName(CommonItem item)	{		QueryItem query = queryItemCommonUtil.toPojo(item);		return bo.getByName(query.getTitle());	}}