package com.shineon.coder.service.dto ;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.db.sql.pojo.RmtUserInfo;import com.shineon.coder.service.bo.UserBO;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service@Slf4jpublic class UserDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	UserBO bo;		public RmtUserInfo get(CommonItem item){		QueryItem query = queryItemCommonUtil.toPojo(item);		return bo.get(query.getId());	}		public RmtUserInfo edit(CommonItem item){		QueryItem query = queryItemCommonUtil.toPojo(item);		RmtUserInfo userInfo = bo.get(query.getId());		userInfo.setDisplayname(query.getTitle());		bo.update(userInfo);		return userInfo;	}		public List<RmtUserInfo> list(CommonItem item){		QueryItem query = queryItemCommonUtil.toPojo(item);		return bo.list(query.getStatus1());	}}