package com.shineon.coder.service.dto ;import com.shineon.coder.db.sql.pojo.RmtUserInfo;import com.shineon.coder.service.bo.UserBO;import com.shineon.coder.service.convert.util.RmtUserInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service@Slf4jpublic class UserDTO extends RmtUserInfoCommonUtil {		@Autowired	UserBO bo;		public RmtUserInfo get(String id){return bo.getUser(id);}		public RmtUserInfo edit(RmtUserInfo user){		bo.update(user);		return user;	}		public List<RmtUserInfo> list(){return bo.list();}}