package com.shineon.coder.action ;import com.shineon.coder.service.convert.util.RmtPrivilegeInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.bind.annotation.RequestMapping;import com.shineon.coder.service.convert.CommonItem;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.dto.PrivilegeDTO;import com.shineon.coder.kernel.constant.action.PrivilegeControllerConstant;import org.springframework.web.bind.annotation.RequestBody;@RestController@Slf4jpublic class PrivilegeController {	
	@Autowired	PrivilegeDTO dto;	
	@Autowired	RmtPrivilegeInfoCommonUtil commonUtil;	
	@RequestMapping(PrivilegeControllerConstant.ACTION_GET)	public CommonItem get(@RequestBody CommonItem item)throws Exception{		return commonUtil.toCommon(dto.get(item));	}	
	@RequestMapping(PrivilegeControllerConstant.ACTION_EDIT)	public CommonItem edit(@RequestBody CommonItem item)throws Exception{		return commonUtil.toCommon(dto.edit(item));	}	
	@RequestMapping(PrivilegeControllerConstant.ACTION_LIST)	public CommonItem list(@RequestBody CommonItem item)throws Exception{		return commonUtil.toCommon(dto.list(item));	}	
	@RequestMapping(PrivilegeControllerConstant.ACTION_DELETE)	public CommonItem delete(@RequestBody CommonItem item) throws Exception{		dto.delete(item);		return commonUtil.success();	}}