package com.shineon.coder.service.convert.base ;import java.util.Date;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import com.shineon.coder.service.convert.CommonItemUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class QueryItemCommonBase implements CommonItemUtils<QueryItem>{	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( QueryItem pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setTitle(pojo.getTitle());		data.setStatus1(pojo.getStatus1());		data.setStatus2(pojo.getStatus2());		data.setStatus3(pojo.getStatus3());		data.setBeginTime(pojo.getBeginTime());		data.setEndTime(pojo.getEndTime());		return data;	}	
	private QueryItem toPojoData( CommonData data) {		QueryItem pojo = new QueryItem();		pojo.setId(data.getId());		pojo.setTitle(data.getTitle());		pojo.setStatus1(data.getStatus1());		pojo.setStatus2(data.getStatus2());		pojo.setStatus3(data.getStatus3());		pojo.setBeginTime(data.getBeginTime());		pojo.setEndTime(data.getEndTime());		return pojo;	}	
	public CommonItem toCommon( QueryItem pojo) {		return success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<QueryItem> pojos) {		List<CommonData> result = new ArrayList();		for(QueryItem item : pojos){			result.add(toCommonData(item));		}		return success(result);	}	
	public QueryItem toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<QueryItem> toPojoList(  CommonItem item) {		List<QueryItem> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}