package com.shineon.coder.service.convert.base ;import java.util.Date;;import com.shineon.coder.db.pojo.ShineonBaseRegion;;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class ShineonBaseRegionCommonBase {	@Autowired	CommonItemUtils utils;	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( ShineonBaseRegion pojo) {		CommonData data = new CommonData();		data.setName(pojo.getRgname());		data.setCreateTime(pojo.getTimecreated());		data.setBeginTime(pojo.getTimemodified());		data.setStatus(pojo.getFlagdeleted());		data.setEndTime(pojo.getTimedeleted());		return data;	}	
	private ShineonBaseRegion toPojoData( CommonData data) {		ShineonBaseRegion pojo = new ShineonBaseRegion();		pojo.setRgname(data.getName());		pojo.setTimecreated(data.getCreateTime());		pojo.setTimemodified(data.getBeginTime());		pojo.setFlagdeleted(data.getStatus());		pojo.setTimedeleted(data.getEndTime());		return pojo;	}	
	public CommonItem toCommon( ShineonBaseRegion pojo) {		return utils.success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<ShineonBaseRegion> pojos) {		List<CommonData> result = new ArrayList();		for(ShineonBaseRegion item : pojos){			result.add(toCommonData(item));		}		return utils.success(result);	}	
	public ShineonBaseRegion toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<ShineonBaseRegion> toPojoList(  CommonItem item) {		List<ShineonBaseRegion> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}