package com.shineon.coder.service.dto ;import com.shineon.coder.db.pojo.RmtUserInfo;import com.shineon.coder.service.feign.OrganizationFeign;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtOrganizationInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtOrganizationInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.CommonItem;import java.util.Date;import java.util.List;@Service@Slf4jpublic class OrganizationDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	OrganizationFeign feign;	@Autowired	RmtOrganizationInfoCommonUtil commonUtil;		public RmtOrganizationInfo get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.get(item));	}		public RmtOrganizationInfo edit(CommonItem item) throws Exception{		RmtOrganizationInfo organizationInfo = commonUtil.toPojo(item);		organizationInfo.setModifyuserid("0");		organizationInfo.setModifytime(new Date());		CommonItem result = feign.edit(commonUtil.toCommon(organizationInfo));		return commonUtil.toPojo( result);	}		public List<RmtOrganizationInfo> list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojoList(feign.list(item));	}		public RmtOrganizationInfo delete(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		feign.delete(item);		return commonUtil.toPojo(item);	}}