package com.shineon.coder.service.dto ;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtPrivilegeInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtPrivilegeInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.CommonItem;import java.util.List;import com.shineon.coder.service.feign.PrivilegeFeign;@Service@Slf4jpublic class PrivilegeDTO {	
	@Autowired	QueryItemCommonUtil queryItemCommonUtil;	
	@Autowired	RmtPrivilegeInfoCommonUtil commonUtil;	
	@Autowired	PrivilegeFeign feign;	
	public RmtPrivilegeInfo get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.get(item));	}	
	public RmtPrivilegeInfo edit(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.edit(item));	}	
	public List<RmtPrivilegeInfo> list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojoList(feign.list(item));	}	
	public RmtPrivilegeInfo delete(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.delete(item));	}}