package com.shineon.coder.service.dto ;import com.shineon.coder.service.feign.PrivilegeGroupFeign;import org.springframework.stereotype.Service;import com.shineon.coder.service.convert.util.RmtPrivilegeGroupInfoCommonUtil;import lombok.extern.slf4j.Slf4j;import com.shineon.coder.db.pojo.RmtPrivilegeGroupInfo;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.CommonItem;import java.util.List;@Service@Slf4jpublic class PrivilegeGroupDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;	@Autowired	PrivilegeGroupFeign feign;	@Autowired	RmtPrivilegeGroupInfoCommonUtil commonUtil;	public RmtPrivilegeGroupInfo get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.get(item));	}		public RmtPrivilegeGroupInfo edit(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.edit(item));	}		public List<RmtPrivilegeGroupInfo> list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return  commonUtil.toPojoList(feign.list(item));	}		public void delete(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		feign.delete(item);	}}