package com.shineon.coder.service.dto;import com.shineon.coder.db.pojo.QueryItem;import com.shineon.coder.db.pojo.RmtUserInfo;import com.shineon.coder.service.convert.util.QueryItemCommonUtil;import com.shineon.coder.service.convert.util.RmtUserInfoCommonUtil;import com.shineon.coder.service.feign.UserFeign;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service@Slf4jpublic class UserDTO1 {		@Autowired	UserFeign userFeign;	@Autowired	QueryItemCommonUtil commonUtil;	@Autowired	RmtUserInfoCommonUtil userInfoCommonUtil;		public RmtUserInfo get(QueryItem item){return null;}		public RmtUserInfo edit(QueryItem item) throws Exception {		return userInfoCommonUtil.toPojo( userFeign.edit(commonUtil.toCommon(item)));	}		public List<RmtUserInfo> list(QueryItem item) throws Exception {		item = new QueryItem();		return userInfoCommonUtil.toPojoList( userFeign.list(commonUtil.toCommon(item)));	}}