package com.shineon.coder.service.convert.base ;import java.util.Date;;import com.shineon.coder.db.sql.pojo.ShineonUserAttached;;import com.shineon.coder.service.convert.CommonData;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.shineon.coder.service.convert.CommonItemUtils;import com.shineon.coder.service.convert.CommonItem;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class ShineonUserAttachedCommonBase {	@Autowired	CommonItemUtils utils;	Logger logger = LoggerFactory.getLogger(getClass());	
	private CommonData toCommonData( ShineonUserAttached pojo) {		CommonData data = new CommonData();		data.setId(pojo.getId());		data.setParent(pojo.getUserid());		data.setCreateTime(pojo.getTimelastlogin());		data.setBeginTime(pojo.getIplastlogin());		return data;	}	
	private ShineonUserAttached toPojoData( CommonData data) {		ShineonUserAttached pojo = new ShineonUserAttached();		pojo.setId(data.getId());		pojo.setUserid(data.getParent());		pojo.setTimelastlogin(data.getCreateTime());		pojo.setIplastlogin(data.getBeginTime());		return pojo;	}	
	public CommonItem toCommon( ShineonUserAttached pojo) {		return utils.success(toCommonData(pojo));	}	
	public  CommonItem toCommon( List<ShineonUserAttached> pojos) {		List<CommonData> result = new ArrayList();		for(ShineonUserAttached item : pojos){			result.add(toCommonData(item));		}		return utils.success(result);	}	
	public ShineonUserAttached toPojo( CommonItem item) {		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}	
	public List<ShineonUserAttached> toPojoList(  CommonItem item) {		List<ShineonUserAttached> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}		
}