package com.shineon.coder.service.mq.service ;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.cloud.stream.annotation.EnableBinding;import org.springframework.cloud.stream.annotation.StreamListener;import org.springframework.stereotype.Component;import com.shineon.coder.service.mq.client.Matao3MessageClient;@Component@EnableBinding(Matao3MessageClient.class)public class Matao3MessageService {	Logger logger = LoggerFactory.getLogger(Matao3MessageService.class);	@StreamListener(Matao3MessageClient.INPUTNAME)	public void receive(String message)	{		logger.info("收到消息，请处理："+message);	}}